import React from 'react';

const HUB_STATUS_LOADING = 'loading';
const HUB_STATUS_CONNECTED = 'connected';
const HUB_STATUS_ERROR = 'error';

const LIGHTS_STATUS_LOADING = 'loading';
const LIGHTS_STATUS_ERROR = 'error';

class Welcome extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
        hub: false,
        lights: false,
    }
  }

  componentDidMount() {
    fetch('/api/discover/config')
        .then(res => res.json())
        .then(config => {
            this.setState({
                loading: false,
                hub: config,
            });
        })
  }

  discoverHub() {
    this.setState({
        hub: HUB_STATUS_LOADING
    });
    fetch('/api/discover/hubs').then(res => {
        if (res.status === 200) {
            this.setState({
                hub: HUB_STATUS_CONNECTED,
            });
            this.discoverLights();
        } else {
            this.setState({
                hub: HUB_STATUS_ERROR
            });
        }
    });
  }

  discoverLights() {
    this.setState({
        lights: LIGHTS_STATUS_LOADING
    });
    fetch('/api/lights/list').then(res => res.json()).then(json => {
        if (json && json.length > 0) {
            this.setState({lights: json});
        }
    }).catch(e => {
        this.setState({lights: LIGHTS_STATUS_ERROR});
    });
  }

  render() {


    if (this.state.lights.length > 0) {
        console.log(this.state.lights);
    }

    return (

        <section className="welcome">
            {!this.state.hub && (
                <>
                    <p>Let's get started, we are going to need to find your Hue hub to authenticate with it.</p>
                    <button
                        onClick={this.discoverHub.bind(this)}
                    >Let's Go</button>
                </>
            )}

            {this.state.hub === HUB_STATUS_LOADING && (
                <>
                    <p>Searching...</p>
                    <p>Please push the button on the hub you want to use.</p>
                </>
            )}

            {this.state.hub === HUB_STATUS_ERROR && (
                <>
                    <p>Oops something went wrong, try again prehaps you weren't quick enough?</p>
                    <button
                        onClick={this.discoverHub.bind(this)}
                    >Let's Go</button>
                </>
            )}

            {this.state.hub && (
                <>
                {this.state.lights === LIGHTS_STATUS_LOADING && (
                    <p>Wahoo! We are connected, just fetching your lights</p>
                )}
                {this.state.lights.length > 0 && (
                    <p>We've found {this.state.lights.length} lights, please select the ones you would like to use:</p>
                )}
                </>
            )}



        </section>

    )


    // connect to hub
    // hub found
    // discover lights


    if (this.state.loading === true) {
      return 'Loading';
    }

    if (this.state.validConfig) {
      return 'Valid Config';
    }

    if (!this.state.validConfig) {
      return 'Invalid Config';
    }
  }

}

export default Welcome;
