import React from 'react';
import Welcome from './components/welcome';

class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      loading: true,
      validConfig: false,
    }
  }

  componentDidMount() {
    fetch('/api/discover/config')
      .then(res => res.json())
      .then(config => {
        this.setState({
          loading: false,
          validConfig: config,
        });
      })
  }

  render() {
    if (this.state.loading === true) {
      return 'Loading';
    }

    if (this.state.validConfig) {
      return 'Valid Config';
    }

    if (!this.state.validConfig) {
      // start discovery
      return (
        <Welcome />
      )
    }
  }

}

export default App;
